import "../styles/App.scss";
import { useEffect, useState } from "react";
import callToApi from "../services/api";
//import localStorage from '../services/localstorage';

function App() {
  // api

  const [data, setData] = useState({});


  useEffect(() => {
    callToApi().then((response) => {
      data(response);
    });
  }, []);



  return (
    // HTML

    <div className="app">
      <header className="header">
        <h1 className="header__title">Adalabers</h1>
        {/*<form>
          <input
            className="header__search"
            autoComplete="off"
            type="search"
            name="search"
            placeholder="Ej. MariCarmen"
            value={search}
            onChange={handleSearchInput}
          />
           <select onChange={handleSelectInput} className='search__select'>
            <option value='all'>Cualquiera</option>
            <option value={}>{nombre adalabers}</option>
          </select>
        </form>*/}
      </header>
      <main>
        <table>
          <thead>
            <tr>
              <th>Columna 1</th>
              <th>Columna 2</th>
              <th>Columna 3</th>
              <th>Columna 4</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Columa 1 fila 1</td>
              <td>Columa 1 fila 1</td>
              <td>Columa 1 fila 1</td>
            </tr>
          </tbody>
        </table>
        <form className="new-adalaber__form">
          <h2 className="new-adalaber__title">Añadir una adalaber</h2>
          <label>
          <input
            className="new-adalaber__input"
            type="text"
            name="name"
            id="name"
            placeholder="Nombre"
            value={newAdalaber.name}
            onChange={handleNewAdalaber}
          /></label>
          <label>
          <input
            className="new-adalaber__input"
            type="text"
            name="tutor"
            id="tutor"
            placeholder="Tutora"
            value={newAdalaber.tutor}
            onChange={handleNewAdalaber}
          /></label>
          <label></label>
          <input
            className="new-adalaber__input"
            type="text"
            name="skill"
            id="skill"
            placeholder="Python, React, IA..."
            value={newAdalaber.skill}
            onChange={handleNewAdalaber}
          />
          <input
            className="new-adalaber__btn"
            type="submit"
            value="Añadir"
            onClick={handleNewAdalaberClick}
          />
        </form>
      </main>
    </div>
  );
}

export default App;
